import React,{useEffect, useState} from 'react'
import NewsItems from './NewsItems'
import Gif from './Gif';
import Proptypes from 'prop-types'
import InfiniteScroll from 'react-infinite-scroll-component';

const News = (props) => {
  const [articles, setArticles] = useState([])
  const [loading, setLaoding] = useState(true)
  const [page, setPage] = useState(1)
  const [totalResults, setTotalResult] = useState(0)
  // document.title = `${this.capitalizeFirstLetter(props.category)} - NewsMokey`;
  // articles = []
  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

  const updateNews =async()=> {
    props.setProgress(10);
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=${page}&pageSize=${props.pageSize}`;
    setState({ loading: true });
    props.setProgress(30);
    let data = await fetch(url);
    props.setProgress(50);
    let parseData = await data.json()
    setArticles(parseData.articles);
    setTotalResult(parseData.totalResults)
    setLaoding(false)
    props.setProgress(100);

  }
  useEffect(() => {
    updateNews();
  }, [])
  const handlePrevclick = async () => {

    setPage(page-1)
    updateNews();
  }
  const handleNextclick = async () => {
    setPage(page+1)
    updateNews();

  }
  const fetchData = async() =>{
    setPage({page: page +1});
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=${page}&pageSize=${props.pageSize}`;
    setLaoding(true)
    let data = await fetch(url);
    let parseData = await data.json()
    setArticles(articles.concat(parseData.articles))
    setTotalResult(parseData.totalResults)
    // setLaoding(true);

  }
return (
      <div className='container my-3'>
        <h2 className='mt-2'>NewsMonkey - Top {capitalizeFirstLetter(props.category)} Headlines</h2>
       
        <InfiniteScroll
            dataLength={articles.length} //This is important field to render the next data
            next={fetchData}
            hasMore={articles.length !== totalResults}
            loader={<Gif/>}>
            <div className="container">
              <div className="row mt-5">
                  {articles.map((element) => {
                    return <div className="col md-4" key={element.url}>
                      <NewsItems title={element.title ? element.title : ""} description={element.description ? element.description : ""} imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
                    </div>
                  })}


              </div>
        </div>
        </InfiniteScroll>
      </div>
    )
}
News.defaultProps = {
  country: 'in',
  pageSize: 5,
  category: 'general',

}
News.propTypes = {
  country: Proptypes.string,
  pageSize: Proptypes.number,
  category: Proptypes.string

}
export default News
