import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class News extends Component {
  
  articles = []
  constructor(){
    super();
    console.log("I am a constructor from news");
    this.state ={
      articles : this.articles,
      loading: false,
      page:1
    }
  }
  async componentDidMount(){
    console.log("cdm")
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=1pageSize=10";
    let data = await fetch(url);
    let parseData = await data.json ()
    console.log(parseData);
    this.setState({articles : parseData.articles, totalResults: parseData.totalResults})
  }
  handlePrevclick = async()=>
  {
    console.log("previous");
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=${this.state.page - 1}&pageSize=10`;
    let data = await fetch(url);
    let parseData = await data.json ()
    console.log(parseData);
    this.setState({
      page: this.state.page - 1,
      articles : parseData.articles
    })
  }
  handleNextclick = async()=>
  {
    console.log("next");
    if(this.state.page + 1 > Math.ceil(this.state.totalResults/10)){
      
    }
    else{
      let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=${this.state.page + 1}&pageSize=10`;
      let data = await fetch(url);
      let parseData = await data.json ()
      console.log(parseData);
      this.setState({
        page: this.state.page + 1,
        articles : parseData.articles
      })
    }
  }
  render() {
    return (
      <div className='container my-3'>
        <h1 className='mt-2'>NewsMonkey - Top Headlines</h1>
        <div className="row">
          {this.state.articles.map((element)=>{
            return <div className="col md-4"  key={element.url}>
            <NewsItems title={element.title?element.title:""} description={element.description?element.description:""} imageUrl={element.urlToImage}  newsUrl={element.url}/>
            </div>
          })}
          
         
        </div>
        <div className="container d-flex justify-content-between">
        <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevclick}>&larr; Previous</button>
        <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/10)} type="button" className="btn btn-dark" onClick={this.handleNextclick}>Next &rarr;</button>
        </div>
        
      </div>
    )
  }
}

export default News
