import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class News extends Component {
  
  articles = []
  constructor(){
    super();
    console.log("I am a constructor from news");
    this.state ={
      articles : this.articles,
      loading: false,
      page:1
    }
  }
  async componentDidMount(){
    console.log("cdm")
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=1";
    let data = await fetch(url);
    let parseData = await data.json ()
    console.log(parseData);
    this.setState({articles : parseData.articles})
  }
  render() {
    return (
      <div className='container my-3'>
        <h1 className='mt-2'>NewsMonkey - Top Headlines</h1>
        <div className="row">
          {this.state.articles.map((element)=>{
            return <div className="col md-4"  key={element.url}>
            <NewsItems title={element.title?element.title:""} description={element.description?element.description:""} imageUrl={element.urlToImage}  newsUrl={element.url}/>
            </div>
          })}
          
         
        </div>
        <div className="container">
          
        </div>
        
      </div>
    )
  }
}

export default News
