import React, { useEffect, useState } from 'react'
import NewsItems from './NewsItems'
import Gif from './Gif';
import Proptypes from 'prop-types'
import InfiniteScroll from 'react-infinite-scroll-component';

const News= (props)=> {

  const [articles, setArticles] = useState([])
  const [loading, setLoading] = useState(true)
  const [page, setPage] = useState(1)
  const [totalResults, settotalResults] = useState(0)
  document.title = `${this.capitalizeFirstLetter(props.category)} - NewsMokey`;

  // articles = []
  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  const updateNews = async ()=> {
    props.setProgress(10);
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=${page}&pageSize=${props.pageSize}`;
    setLoading(true)
    props.setProgress(30);
    let data = await fetch(url);
    props.setProgress(50);
    let parseData = await data.json()
    console.log(parseData);
    setArticles(parseData.articles);
    settotalResults(parseData.totalResults)
    setLoading(false)
    props.setProgress(100);

  }
  useEffect(() => {
        this.updateNews();

  }, [])
  
  fetchData = async() =>{
    this.setState({page: page +1});
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=12b57f3f5a6b4ba0a04e4df8a2627c02&page=${page}&pageSize=${props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parseData = await data.json()
    console.log(parseData);
    this.setState({
      articles: articles.concat(parseData.articles),
      totalResults: parseData.totalResults,
      loading: false
    })

  }

    return (
      <>
        <h2 className='mt-2'>NewsMonkey - Top {this.capitalizeFirstLetter(props.category)} Headlines</h2>
        {/* {loading && <Gif/>} */}
        <InfiniteScroll
            dataLength={articles.length} //This is important field to render the next data
            next={this.fetchData}
            hasMore={articles.length !== totalResults}
            loader={<Gif/>}>
            <div className="container">
              <div className="row mt-5">
                  {articles.map((element) => {
                    return <div className="col md-4" key={element.url}>
                      <NewsItems title={element.title ? element.title : ""} description={element.description ? element.description : ""} imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
                    </div>
                  })}


              </div>
        </div>
        </InfiniteScroll>
      </>
    )
  
}
News.defaultProps = {
  country: 'in',
  pageSize: 5,
  category: 'general',

}
News.propTypes = {
  country: Proptypes.string,
  pageSize: Proptypes.number,
  category: Proptypes.string

}
export default News
